mapper.select:
select *
from sys_menu


        and type = #{type}


        and status = #{status}


        and is_show = #{isShow}


        and (name like concat('%',#{keyword},'%')
        or code like concat('%',#{keyword},'%')
        or route_url like concat('%',#{keyword},'%')
        or component_path like concat('%',#{keyword},'%')
        or route_redirect like concat('%',#{keyword},'%')
        )


order by sort
