mapper.select:
select u.*, r.name roleName, r.is_system roleIsSystem from sys_user u inner join sys_role r on u.role_id = r.id
and r.id = #{roleId}
and u.status = #{status}
and date(u.create_time) >= date(#{startCreateTime})
and date(u.create_time) <= date(#{endCreateTime})
and (u.username like concat('%',#{keyword},'%') or u.nickname like concat('%',#{keyword},'%') or u.phone like concat('%',#{keyword},'%') or u.email like concat('%',#{keyword},'%'))

select u.*
from sys_user u
where u.username = #{username}

select count(1)
from sys_user
where role_id = #{roleId}
